import React, { useState } from 'react'
import './style.css'
import { Link } from 'react-router-dom'

export const Login = () => {
    const [focus, setFocus] = useState('');

    const onFocusHandle = () => {
        setFocus('focus')
    }

    const onBlurHandle = () => {
        if (focus === '') {
            setFocus('')
        }
    }

    return (
        <form className='login-form'>
            <h1>Login</h1>
            <div className='txtb'>
                <input type='text' onFocus={onFocusHandle} className={focus} onBlur={onBlurHandle} />
                <span data-placeholder='Username'></span>
            </div>

            <div className='txtb'>
                <input type="password" onFocus={onFocusHandle} className={focus} onBlur={onBlurHandle}/>
                <span data-placeholder='Password'></span>
            </div>

            <input type="submit" className='logbtn' value='login' />
            
            <div className='bottom-text'>
                Don't have account? <Link to='#'>Sign Up</Link>
            </div>
        </form>
    )
}
